import Head from "next/head";
import Link from "next/link";
import Router from "next/router";
import { Container, Row, Col, Form, Button, Image } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { axiosGeneral } from "../helpers/global";
import { setAccessToken, setUser } from "../store/actionCreator";
import * as Yup from "yup";
import { useFormik } from "formik";
import Loading from "../components/Loading";
import { useToasts } from "react-toast-notifications";
import OTPInput, { ResendOTP } from "otp-input-react";

const FormSchema = Yup.object().shape({
  email: Yup.string().email().required("* Email tidak boleh kosong"),
  password: Yup.string().required("* Password tidak boleh kosong"),
});

export default function Home() {
  const dispatch = useDispatch();
  const accessToken = useSelector((state) => state.accessToken);
  const { addToast } = useToasts();
  const [isLoading, setIsLoading] = useState(false);
  const [OTP, setOTP] = useState();
  const [isOtp, setIsOtp] = useState(false);
  const [tokenTemp, setTokenTemp] = useState("");


  useEffect(() => {
    if (accessToken) {
      Router.replace("/dashboard");
    }
  }, []);

  const doLogin = async (values) => {
    try {
      setIsLoading(true);
      const response = await axiosGeneral.post("/login", values);
      const { status, data } = response;
      // console.log(data);
      if (status === 200) {
        // dispatch(setUser(data.data.user_metadata));
        // dispatch(setAccessToken(data.data.token));
        //Router.replace("/dashboard");
        setTokenTemp(data.data.temp_token);
        setIsLoading(false);
        setIsOtp(true);
      }
    } catch (error) {
      setIsLoading(false);
      addToast(error.response.data.message, { appearance: "error" });
    }
  };

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: FormSchema,
    onSubmit: (values) => doLogin(values),
  });

  const renderButton = (buttonProps) => {
    return (
      <button
        className="px-5 py-2 bg-blue-300 rounded text-white mb-4 text-sm"
        {...buttonProps}
      >
        {buttonProps.remainingTime !== 0
          ? `Kirim Ulang (${buttonProps.remainingTime}s)`
          : "Kirim Ulang"}
      </button>
    );
  };

  const renderTime = (remainingTime) => {
    return <></>;
  };

  const doMatchOtp = async () => {
    try {
      const body = {
        otp: OTP,
        temp_token: tokenTemp,
      };
      console.log(body);
      setIsLoading(true);

      const response = await axiosGeneral.post("/verify-otp", body);
      const { status, data } = response;
      //console.log(status);
      if (status === 200) {
        dispatch(setUser(data.data.user_metadata));
        dispatch(setAccessToken(data.data.token));
        Router.replace("/dashboard");

        setIsLoading(false);
      } else if (status === 500) {
        setIsLoading(false);
      }
    } catch (error) {
      setIsLoading(false);
      addToast("Gagal melanjutkan", { appearance: "error" });
    }
  };

  const doResendOtp = async () => {
    try {
      const body = {
        temp_token: tokenTemp,
      };
      console.log(tokenTemp);
      setIsLoading(true);

      const response = await axiosGeneral.post("/resend-otp", body);

      const { status, data } = response;
      if (status === 200) {
        addToast("Silahkan cek email anda", { appearance: "success" });
        setIsLoading(false)
      } else if (status === 500) {
        setIsLoading(false);
      }
    } catch (error) {
      setIsLoading(false);
      addToast("kesalahan request data", { appearance: "error" });
    }
  };

  return (
    <>
      <Head>
        <title>Login - E-Survey</title>
      </Head>
      {isLoading && <Loading />}
      <Container fluid>
        <Row className="overflow-hidden">
          <Col className="col-left" xl="3" lg="3">
            {isOtp == false ? (
              <>
                <div className="on-center">
                  <h1>E-Survey</h1>
                  <h3 className="font-semibold">Masuk</h3>
                  <form onSubmit={formik.handleSubmit} method="POST">
                    <Form.Group className="mt-5">
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        className="custom-input"
                        type="email"
                        autoComplete="false"
                        name="email"
                        onChange={formik.handleChange}
                        value={formik.values.email}
                      />
                      {formik.errors.email && (
                        <span className="text-xs text-red-500">
                          {formik.errors.email}
                        </span>
                      )}
                    </Form.Group>
                    <Form.Group>
                      <Form.Label htmlFor="password">Password</Form.Label>
                      <Form.Control
                        className="custom-input"
                        type="password"
                        autoComplete="false"
                        name="password"
                        onChange={formik.handleChange}
                        value={formik.values.password}
                      />
                      {formik.errors.password && (
                        <span className="text-xs text-red-500">
                          {formik.errors.password}
                        </span>
                      )}
                    </Form.Group>
                    <Link href="/forgot-password">
                      <span className="forgot-password-link">
                        Lupa Password?
                      </span>
                    </Link>
                    <Button
                      disabled={formik.isSubmitting}
                      variant="warning"
                      type="submit"
                      className="mt-4"
                      block
                    >
                      Login
                    </Button>
                  </form>
                </div>
              </>
            ) : (
              <div className="on-center">
                <ResendOTP
                  renderButton={renderButton}
                  renderTime={renderTime}
                  maxTime={90}
                  style={{ justifyContent: "center" }}
                  onResendClick={() => doResendOtp()}
                />
                <OTPInput
                  value={OTP}
                  onChange={(value) => setOTP(value)}
                  autoFocus
                  OTPLength={5}
                  otpType="number"
                  inputClassName="outline-none rounded font-bold mx-2"
