import Select from "react-select";
import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { axiosGeneral, errorHandler } from "../../helpers/global";
import { useToasts } from "react-toast-notifications";
import * as Yup from "yup";
import { useFormik } from "formik";
import styled from "styled-components";

const Card = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0px 5px 15px 4px rgba(0, 0, 0, 0.25);
`;

function AddPembayaran({ show, setShow }) {
  const accessToken = useSelector((state) => state.accessToken);
  const user = useSelector((state) => state.user);
  const { addToast } = useToasts();
  const [pelangganOption, setPelangganOption] = useState([]);
  const [tagihanOption, setTagihanOption] = useState([]);

  const [id_tagihan, setIdTagihan] = useState();
  const [id_pelanggan, setIdPelanggan] = useState();
  const [tanggal_pembayaran, setTanggalBayar] = useState();
  const [bulan_bayar, setBulanBayar] = useState();
  const [biaya_admin, setBiayaAdmin] = useState();
  const [total_bayar, setTotalBayar] = useState();
  const [id_user, setIdUser] = useState();

  const optionsBulan = [
    { value: 'January', label: 'January' },
    { value: 'February', label: 'February' },
    { value: 'March', label: 'March' },
    { value: 'April', label: 'April' },
    { value: 'May', label: 'May' },
    { value: 'June', label: 'June' },
    { value: 'July', label: 'July' },
    { value: 'August', label: 'August' },
    { value: 'September', label: 'September' },
    { value: 'October', label: 'October' },
    { value: 'November', label: 'November' },
    { value: 'December', label: 'December' }
  ]

  useEffect(() => {
    fetchTagihan();
  }, []);

  const fetchTagihan = async () => {
    try {
      const headers = {
        Authorization: accessToken,
      };

      const response = await axiosGeneral.get("/resources/tagihan", {
        headers,
        params: {
          start: 0,
          limit: 999,
        },
      });
      setTagihanOption([]);
      const { status, data } = response;
      if (status === 200) {
        const tagihanArr = [];
        const tagihan = data.data;
        for (const iterator of tagihan) {
          let val = {
            value: iterator.id_tagihan,
            label: iterator.id_penggunaan + " " + iterator.bulan + " " + iterator.tahun,
          };
          tagihanArr.push(val);
        }
        setTagihanOption(tagihanArr);
      }
    } catch (error) {
      addToast(errorHandler(error), { appearance: "error" });
    }
  };

  const createPembayaran = async (values) => {
    try {
      const headers = {
        Authorization: accessToken,
      };
      const body = {
        id_tagihan: values.id_tagihan,
        id_pelanggan: values.id_pelanggan,
        tanggal_pembayaran: new Date(values.tanggal_pembayaran),
        bulan_bayar: values.bulan_bayar,
        biaya_admin: values.biaya_admin,
        total_bayar: values.total_bayar,
        id_user: user.id_user,
      }
      
      
      const response = await axiosGeneral.post(`/resources/pembayaran`, 
      values, {
        headers,
      });
      const { status } = response;
      if (status === 201 || status === 200) {
        setShow(!show);
      }
      addToast("Berhasil simpan data", { appearance: "success" });
    } catch (error) {
      addToast(errorHandler(error), { appearance: "error" });
    }
  };

  return (
    <Card className="w-3/4 h-4/5 block mx-auto px-4 my-3">
      <div className="block mx-auto py-4">
        <h1 className="font-bold text-3xl text-black mb-16">
          Tambah Data Pembayaran
        </h1>
        <form>
        <div className="col-span-2 my-4">
            <label htmlFor="email" className="block font-semibold text-sm">
              Tagihan
            </label>
            <Select
              placeholder="Pilih tagihan"
              options={tagihanOption}
              name="id_tagihan"
              isClearable={true}
              defaultValue={tagihanOption.find(
                (v) => v.value === id_tagihan
              )}
              onChange={(e) => {
                const val = e ? e.value : null;
                setIdTagihan(val);
              }}
            />
          </div>
          <div className="flex justify-between">
            <div className="w-full">
              <label htmlFor="email" className="block font-semibold text-sm">
                Nomor KWH
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
            <div className="w-full mx-2">
              <label htmlFor="email" className="block font-semibold text-sm">
                Nama Pelanggan
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
            <div className="w-full">
              <label htmlFor="email" className="block font-semibold text-sm">
                Status
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
          </div>
        
          <div className="flex justify-between">
            <div className="w-full">
              <label htmlFor="email" className="block font-semibold text-sm">
                KWH Meter Awal
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
            <div className="w-full ml-2">
              <label htmlFor="email" className="block font-semibold text-sm">
                KWH Meter Akhir
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
          </div>

          <div className="flex justify-between">
            <div className="w-full mr-2">
              <label htmlFor="email" className="block font-semibold text-sm">
                Penggunaan KWH meter
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                // value={nama_pelanggan}
              />
            </div>
            <div className="w-5 mr-2">
              <label htmlFor="email" className="block text-black font-semibold text-sm">
                X
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-5 focus:outline-none text-base"
                name="id_pelanggan"
                value="X"
              />
            </div>
            <div className="w-full mr-2">
              <label htmlFor="email" className="block font-semibold text-sm">
                Tarif perKWH
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
            <div className="w-5">
              <label htmlFor="email" className="block text-black font-semibold text-sm">
                =
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-5 focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                value="+"
              />
            </div>
            <div className="w-full">
              <label htmlFor="email" className="block font-semibold text-sm">
                Biaya
              </label>
              <input
                className="py-2 px-3 bg-gray-100 rounded block w-full focus:outline-none text-base"
                name="id_pelanggan"
                isClearable={true}
                // value={nama_pelanggan}
              />
            </div>
          </div>

          <div className="flex flex-row justify-end my-4">
            <div
              onClick={() => setShow(!show)}
              className="px-12 mx-3 text-white py-2 text-center w-1/6 font-semibold bg-gray-400 rounded cursor-pointer"
            >
              Batal
            </div>
            <button
              type="submit"
              //   style={{ background: "#4361ee" }}
              className="inline-block py-2 text-gray-50 bg-blue-500 px-4 text-center w-1/6 rounded font-bold cursor-pointer focus:outline-none"
            >
              Simpan
            </button>
          </div>
        </form>
      </div>
    </Card>
  );
}

export default AddPembayaran;
