import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { MdToc, MdAdd, MdEdit, MdDelete } from "react-icons/md";
import Modal from "../../components/Modal";
import Input from "../../components/Input";
import TextArea from "../../components/TextArea";
import Button from "../../components/Button";
import { axiosGeneral } from "../../helpers/global";
import MainLayout from "../../components/MainLayout";
import KegiatanRow from "../../components/kuesioner/KegiatanRow";
import Pagination from "react-js-pagination";
import { ChevronLeft, ChevronRight } from "@material-ui/icons";
import Select from "react-select";
import * as Yup from "yup";
import { useFormik } from "formik";
import { useToasts } from "react-toast-notifications";
import { setLoading } from "../../store/actionCreator";
import { useDispatch, useSelector } from "react-redux";

const FormSchema = Yup.object().shape({
  password: Yup.string().required("* Password tidak boleh kosong"),
  ulangi_password: Yup.string().oneOf(
    [Yup.ref("password"), null],
    "Passwords don't match!"
  ),
});

export default function User() {
  const [showModal, setShowModal] = useState(false);
  const [jabatanDropdown, setJabatanDropdown] = useState([]);
  const [selectedJabatan, setSelectedJabatan] = useState(0);
  const [dataInstasi, setDataInstansi] = useState([]);
  const [selectedInstansi, setSelectedInstansi] = useState(0);
  const accessToken = useSelector((state) => state.accessToken);
  const [getNip, setNip] = useState("");
  const [getNama, setNama] = useState("");
  const [getEmail, setEmail] = useState("");
  const [getNohp, setNohp] = useState("");
  const [getRole, setRole] = useState();
  const [selectedAktif, setSelectedAktif] = useState(false);
  const [selectedHakAkses, setSelectedHakAkses] = useState("");
  const dispatch = useDispatch();
  const { addToast } = useToasts();
  const [dataAllUser, setDataAllUser] = useState([]);
  const [haveId, setHaveId] = useState();
  const [isEdit, setIsEdit] = useState(false);

  useEffect(() => {
    if (accessToken != null) {
      GetInstansi();
      GetJabatan();
      GetAllUser();
    }
  }, []);

  const GetAllUser = async () => {
    try {
      const headers = {
        Authorization: accessToken,
      };
      dispatch(setLoading(true));
      const response = await axiosGeneral.get("/resources/pegawai", {
        headers,
      });

      const { status, data } = response;
      if (status === 200) {
        const AllUserArr = [];
        const AllUser = data.data;
        for (const iterator of AllUser) {
          AllUserArr.push(iterator);
        }
        setDataAllUser(AllUserArr);
        dispatch(setLoading(false));
      } else if (status === 500) {
        addToast("Gagal Mengambil Data User", { appearance: "error" });
        dispatch(setLoading(false));
      }
    } catch (error) {
      addToast("Gagal Mengambil Data User", { appearance: "error" });
      dispatch(setLoading(false));
    }
  };

  const GetInstansi = async () => {
    try {
      const headers = {
        Authorization: accessToken,
      };

      const response = await axiosGeneral.get("/resources/instansi", {
        headers,
      });

      const { status, data } = response;
      if (status === 200) {
        const intansiArr = [];
        const instansi = data.data;
        for (const iterator of instansi) {
          let val = {
            value: iterator.id,
            label: iterator.kode_instansi + " - " + iterator.nama_instansi,
          };
          intansiArr.push(val);
        }
        setDataInstansi(intansiArr);
        dispatch(setLoading(false));
      } else if (status === 500) {
        addToast("Gagal Mengambil Data Instansi", { appearance: "error" });
      }
    } catch (error) {
      addToast(error.response.data.message, { appearance: "error" });
      dispatch(setLoading(false));
    }
  };

  const GetJabatan = async () => {
    try {
      const headers = {
        Authorization: accessToken,
      };
      const response = await axiosGeneral.get("/resources/jabatan", {
        headers,
      });
      const { data, status } = response;
      if (status === 200) {
        const jabatanArr = [];
        const jabatan = data.data;
        for (const iterator of jabatan) {
          jabatanArr.push(iterator);
        }
        setJabatanDropdown(jabatanArr);
      } else if (status === 500) {
        addToast("Gagal Mengambil Data Jabatan", { appearance: "error" });
      }
    } catch (error) {
      addToast(error.response.data.message, { appearance: "error" });
    }
  };

  const doSave = async (values) => {
    try {
      if (isEdit == false) {
        const body = {
          nip: getNip,
          nama_lengkap: getNama,
          no_hp: getNohp,
          id_jabatan: parseInt(selectedJabatan),
          id_instansi: parseInt(selectedInstansi),
          email: getEmail,
          password: formik.values.password,
          role: selectedHakAkses,
          active: selectedAktif === "true" ? true : false,
        };

        const headers = {
          Authorization: accessToken,
        };

        dispatch(setLoading(true));
        const response = await axiosGeneral.post(`/resources/pegawai`, body, {
          headers,
        });
        const { status, data } = response;
        if (status === 200) {
          addToast("Berhasil tambah user", { appearance: "success" });
          dispatch(setLoading(false));
          GetInstansi();
          GetJabatan();
          GetAllUser();
        }
      } else if (isEdit == true) {
        const body = {
          nip: getNip,
          nama_lengkap: getNama,
          no_hp: getNohp,
          id_jabatan: parseInt(selectedJabatan),
          id_instansi: parseInt(selectedInstansi),
          email: getEmail,
          role: selectedHakAkses,
          active: selectedAktif === "true" ? true : false,
        };

        const headers = {
          Authorization: accessToken,
        };

        dispatch(setLoading(true));
        const response = await axiosGeneral.put(
          `/resources/pegawai/${haveId}`,
          body,
          {
            headers,
          }
        );
        const { status, data } = response;
        if (status === 200) {
          addToast("Berhasil edit user", { appearance: "success" });
          dispatch(setLoading(false));
          GetInstansi();
          GetJabatan();
          GetAllUser();
        }
      }
    } catch (error) {
      dispatch(setLoading(false));
      addToast("Gagal eksekusi", { appearance: "error" });
    }
  };

  const DoNull = () => {
    setNip();
    setNama();
    setNohp();
    setSelectedJabatan();
    setSelectedInstansi();
    setEmail();
    setSelectedHakAkses();
    setSelectedAktif();
    console.log(isEdit);
  };

  const doModalEdit = async (id) => {
    try {
      DoNull();
      GetUserById(id);
      setHaveId(id);
      setShowModal(true);
    } catch (error) {
      addToast("Gagal tambah user", { appearance: "error" });
    }
  };

  const GetUserById = async (id) => {
    try {
      const headers = {
        Authorization: accessToken,
      };
      dispatch(setLoading(true));
      const response = await axiosGeneral.get(`/resources/pegawai/${id}`, {
        headers,
      });
      const { status, data } = response;
      if (status === 200) {
        setNip(data.data.nip);
        setNama(data.data.nama_lengkap);
        setNohp(data.data.no_hp);
        setSelectedJabatan(data.data.jabatan.id);
        setSelectedInstansi(data.data.instansi.id);
        setEmail(data.data.account_detail.email);
        setSelectedHakAkses(data.data.account_detail.role);
        setSelectedAktif(data.data.account_detail.active);
        dispatch(setLoading(false));
      } else if (status === 500) {
        addToast("Gagal Mengambil Data User", { appearance: "error" });
        dispatch(setLoading(false));
      }
    } catch (error) {
      addToast("Gagal Mengambil Data User", { appearance: "error" });
      dispatch(setLoading(false));
    }
  };

  const doDelete = async (id) => {
    try {
      const headers = {
        Authorization: accessToken,
      };
      console.log(id);
      dispatch(setLoading(true));
      const response = await axiosGeneral.delete(`/resources/pegawai/${id}`, {
        headers,
      });
      const { status, data } = response;
      if (status === 200) {
        addToast("Berhasil hapus user", { appearance: "success" });
        dispatch(setLoading(false));
        GetInstansi();
        GetJabatan();
        GetAllUser();
      } else {
        addToast("Gagal hapus user", { appearance: "warning" });
        dispatch(setLoading(false));
      }
    } catch (error) {
      addToast("Gagal hapus user", { appearance: "error" });
      dispatch(setLoading(false));
    }
  };

  const formik = useFormik({
    initialValues: {
      password: "",
      ulangi_password: "",
    },
    validationSchema: FormSchema,
    enableReinitialize: true,
  });

  return (
    <MainLayout>
      <div className="pt-10 px-8">
        <div className="flex flex-row justify-between items-center pb-14 w-full">
          <MenuTitle>Users</MenuTitle>
          <ButtonAdd
            className="button"
            onClick={() => {
              setShowModal(true);
              setIsEdit(false);
              DoNull();
            }}
          >
            Tambah User
          </ButtonAdd>
        </div>
        <Card>
          <p className="font-extrabold">Filter</p>
          <div className="flex flex-row w-full mb-10 mr-8">
            <div className="w-1/2 mr-8">
              <Select
                className="w-auto"
                placeholder="Pilih detail instansi"
                value={dataInstasi.find((op) => op.value === selectedInstansi)}
                onChange={(e) => setSelectedInstansi(e.value)}
                options={dataInstasi}
              />
            </div>
            <div className="w-auto mr-8">
              <select
                onChange={(e) => setSelectedJabatan(parseInt(e.target.value))}
                name="jabatan"
                placeholder="jabatan"
                value={selectedJabatan}
                label="Jabatan"
                className="w-full bg-gray-200 rounded-xl py-2 px-2 font-nunito focus:outline-none text-sm md:text-base"
              >
                <option value="0" disabled>Pilih Jabatan</option>
                {jabatanDropdown.map((item, index) => (
                  <option value={item.id} key={index}>
                    {item.nama_jabatan}
                  </option>
                ))}
              </select>
            </div>
            <Input
              className="w-5/6 mr-8"
              label="Detail Instansi"
              placeholderText="NIP atau Nama"
              onChange={(e) => setiDirektorat(e.target.value)}
            />
            <ButtonAdd className="ml-8">Cari</ButtonAdd>
          </div>
          <table className="table-auto">
            <thead className="border-solid border-b-2 border-light-blue-500">
              <tr>
                <th className="font-extrabold py-4 w-12">#</th>
                <th className="font-extrabold py-4 w-28">Nip</th>
                <th className="font-extrabold py-4 w-28">Nama</th>
                <th className="font-extrabold py-4 w-28">Jabatan</th>
                <th className="font-extrabold py-4 w-28">Instansi</th>
                <th className="font-extrabold py-4 w-28">Email</th>
                <th className="font-extrabold py-4 w-28">Hak Akses</th>
                <th className="font-extrabold py-4 w-28">Status</th>
                <th className="font-extrabold py-4 w-28"></th>
              </tr>
            </thead>
            {dataAllUser.map((item, index) => (
              <tbody
                className="border-solid border-b py-2 border-light-blue-500"
                key={index}
              >
                <tr>
                  <th className="font-nunito py-3">{index + 1}.</th>
                  <th className="font-nunito py-3">{item.nip}</th>
                  <th className="font-nunito py-3">{item.nama_lengkap}</th>
                  <th className="font-nunito py-3">{item.jabatan.nama_jabatan}</th>
                  <th className="font-nunito py-3">{item.instansi.nama_instansi}</th>
                  <th className="font-nunito py-3">{item.account_detail.email}</th>
                  <th className="font-nunito py-3">{item.account_detail.role}</th>
                  <th className="font-nunito py-3">
                    {item.account_detail.active === true
                      ? "aktif"
                      : "Non Aktif"}
                  </th>
                  <th className="font-nunito py-3">
                    <div className="flex flex-row mr-4">
                      <div
                        onClick={() => {
                          setIsEdit(true);
                          doModalEdit(item.id);
                        }}
                        className="hover:bg-gray-400 rounded-full w-auto cursor-pointer p-2 w-12 mr-1"
                      >
                        <MdEdit size="24px" color="#565656" />
                      </div>
                      <div
                        onClick={() => doDelete(item.id)}
                        className="hover:bg-gray-400 rounded-full w-auto cursor-pointer p-2 w-12 mr-1"
                      >
                        <MdDelete size="24px" color="#565656" />
                      </div>
                    </div>
                  </th>
                </tr>
              </tbody>
            ))}
          </table>
          <div className="w-full mt-8 flex flex-row justify-center items-center">
            <Pagination
              activePage={15}
              itemsCountPerPage={10}
              totalItemsCount={450}
              pageRangeDisplayed={5}
              onChange={(pageNumber) => console.log(pageNumber)}
              itemClass="bg-blue-100 py-2 px-4 mx-1 rounded font-bold"
              linkClass="text-gray-700 hover:no-underline hover:text-gray-700"
              hideFirstLastPages={true}
              prevPageText={<ChevronLeft />}
              nextPageText={<ChevronRight />}
              activeClass="bg-blue-400"
              activeLinkClass="text-white"
            />
          </div>
        </Card>
        <Modal
          modalSize="lg"
          modalTitle="Tambah User"
          show={showModal}
          onClickBackdrop={() => {
            setShowModal(!showModal);
            setIsEdit(false);
          }}
        >
          <Input
            labelText="Nip"
            valueData={getNip}
            onChange={(e) => setNip(e.target.value)}
          />
          <Input
            labelText="Nama"
            valueData={getNama}
            onChange={(e) => setNama(e.target.value)}
          />
          <Input
            labelText="Email"
            type="email"
            valueData={getEmail}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Input
            labelText="No Handphone"
            valueData={getNohp}
            onChange={(e) => setNohp(e.target.value)}
          />
          <div className="grid grid-cols-2 gap-4 mt-2">
            <div className="my-2">
              <select
                onChange={(e) => setSelectedJabatan(parseInt(e.target.value) || 0)}
                name="jabatan"
                placeholder="jabatan"
                value={selectedJabatan}
                className="w-full bg-gray-200 rounded-xl py-2 px-2 font-nunito focus:outline-none text-sm md:text-base"
              >
                <option value="0" disabled>Pilih Jabatan</option>
                {jabatanDropdown.map((item, index) => (
                  <option value={item.id} key={index}>
                    {item.nama_jabatan}
                  </option>
                ))}
              </select>
            </div>
            <div className="my-2">
              <Select
                placeholder="Pilih detail instansi"
                value={dataInstasi.find((op) => op.value === selectedInstansi)}
                onChange={(e) => setSelectedInstansi(e.value)}
                options={dataInstasi}
              />
            </div>
          </div>
          <div className="grid grid-cols-2 gap-4 mt-2">
            <div className="my-2">
              <label className="font-bold text-black text-sm">Hak Akses</label>
              <select
                value={selectedHakAkses}
                onChange={(e) => setSelectedHakAkses(e.target.value)}
                id="direktorat"
                name="direktorat"
                className="w-full bg-gray-200 rounded-xl py-2 px-4 font-nunito focus:outline-none"
              >
                <option value="">Pilih Hak Akses</option>
                <option value="respondent">Respondent</option>
                <option value="superadmin">Superadmin</option>
                <option value="admin">Admin</option>
              </select>
            </div>
            <div className="my-2">
              <label className="font-bold text-black text-sm">
                User Status
              </label>
              <select
                onChange={(e) => setSelectedAktif(e.target.value)}
                id="direktorat"
                name="direktorat"
                value={selectedAktif}
                className="w-full bg-gray-200 rounded-xl py-2 px-4 font-nunito focus:outline-none"
              >
                <option value="">Pilih status uer</option>
                <option value={true}>Aktif</option>
                <option value={false}>Non-Aktif</option>
              </select>
            </div>
          </div>
          <form onSubmit={formik.handleSubmit}>
            <Input
              labelText="Password"
              classes="my-2"
              placeholder="Password"
              type="password"
              nameFormik="password"
              onChange={formik.handleChange}
              valueData={formik.values.password}
            />
            {formik.errors.password && (
              <span className="text-xs text-red-500">
                {formik.errors.password}
              </span>
            )}
            <Input
              labelText="Ulangi Password"
              classes="my-2"
              placeholder="Ulangi Password"
              type="password"
              nameFormik="ulangi_password"
              onChange={formik.handleChange}
              valueData={formik.values.ulangi_password}
            />
            {formik.errors.ulangi_password && (
              <span className="text-xs text-red-500">
                {formik.errors.ulangi_password}
              </span>
            )}
          </form>
          <Button
            onClick={doSave}
            buttonText="Simpan"
            buttonColor="#4361EE"
            buttonTextColor="#FFF"
            fontSize={14}
            className="mt-4"
            type="submit"
          />
        </Modal>
      </div>
    </MainLayout>
  );
}

