import Select from "react-select";
import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { axiosGeneral, errorHandler } from "../../helpers/global";
import { useToasts } from "react-toast-notifications";
import * as Yup from "yup";
import { useFormik } from "formik";
import styled from "styled-components";

const Card = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0px 5px 15px 4px rgba(0, 0, 0, 0.25);
`;

function AddPembayaran({ show, setShow }) {
  const accessToken = useSelector((state) => state.accessToken);
  const user = useSelector((state) => state.user);
  const { addToast } = useToasts();
  const [tagihanOption, setTagihanOption] = useState([]);

  const [meterAwal, setMeterAwal] = useState([]);
  const [meterAkhir, setMeterAkhir] = useState([]);
  const [nomorKwh, setNomorKwh] = useState();
  const [nama_pelanggan, setNamaPelanggan] = useState();
  const [status, setStatus] = useState();
  const [bulan, setBulan] = useState();
  const [tahun, setTahun] = useState();
  const [penggunaanKwh, setPenggunaanKwh] = useState();
  const [tarif, setTarif] = useState();
  const [biaya, setBiaya] = useState();
  const [biayaAdmin, setBiayaAdmin] = useState();
  const [totalBiaya, setTotalBiaya] = useState();
  const [bayar, setBayar] = useState();
  const [kembalian, setKembalian] = useState();

  useEffect(() => {
    fetchTagihan();
  }, []);

  const getDataTagihanById = async (id_tagihan) => {
    try {
      const headers = {
        Authorization: accessToken,
      };

      const response = await axiosGeneral.get(`/resources/tagihan/${id_tagihan}`, {
        headers,
        params: {
          start: 0,
          limit: 999,
        },
      });
      const { status, data } = response;
      if (status === 200) {
        const akhir = data.data.meter_akhir;
        const awal = data.data.meter_awal;
        const hasil = akhir - awal;
        setPenggunaanKwh(hasil);
        setMeterAkhir(data.data.meter_akhir);
        setMeterAwal(data.data.meter_awal);
      }
    } catch (error) {
      addToast(errorHandler(error), { appearance: "error" });
