      });
      setTagihanOption([]);
      const { status, data } = response;
      if (status === 200) {
        const tagihanArr = [];
        const tagihan = data.data;
        for (const iterator of tagihan) {
          let val = {
            value: iterator.id_tagihan,
            label: iterator.pelanggan_detail.nomor_kwh,
          };
          tagihanArr.push(val);
        }
        setTagihanOption(tagihanArr);
