import Head from "next/head";
import Link from "next/link";
import { Container, Row, Col, Form, Button, Image } from "react-bootstrap";

export default function ForgotPassword() {
  return (
    <>
      <Head>
        <title>Lupa Password - E-Survey</title>
      </Head>
      <Container fluid>
        <Row>
          <Col className="col-left" xl="3" lg="3">
            <div className="on-center">
              <h1>E-Survey</h1>
              <h3>Lupa Password</h3>
              <Form.Group className="mt-5">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  className="custom-input"
                  type="email"
                  autoComplete="false"
                />
              </Form.Group>
              <Button variant="warning" type="submit" className="mt-5" block>
                Kirim
              </Button>
              <Link href="/">
                <Button variant="link" block>
                  Back to Login
                </Button>
              </Link>
            </div>
          </Col>
          <Col className="col-right" xl="9" lg="9">
            <Image
              src="https://www.imigrasi.go.id/uploads/logo/10-52-13-logo-imigrasi_-_Copy.png"
              alt="Logo Imigrasi"
              className="imigrasi-logo"
              fluid
            />
          </Col>
        </Row>
      </Container>
      <style>{`
        .imigrasi-logo {
          width:90%;
          object-fit: contain;
          margin-top:20vh;
          margin-left: auto;
          margin-right: auto;
          display:block;
        }
        .on-center {
          margin-top:60%;
        }
        .col-right {
          background: #03045E;
          height: 100vh;
        }
        .col-left {
          background: #FDFDFD;
          height: 100vh;
          padding-left: 36px;
          padding-right: 36px;
        }
        h1 {
          font-family: "Poppins", sans-serif;
          font-weight: bold;
          font-size: 3.75rem;
          color: #323232;
          text-align:center;
          margin-bottom: 75px;
        }
        h3 {
          font-weight: 600;
        }
        .btn-warning {
          color: #2B2D42 !important;
          font-weight: bold !important;
          border-radius: 10px;
          text-transform: uppercase;
          height:40px;
          font-size: 14px;
        }
        .btn-link {
          text-align: center;
          font-weight: 600;
          color: #889ADA;
        }
        .btn-link:hover, .btn-link:focus {
          text-decoration: none;
          color: #889ADA;
          box-shadow: none;
        }
        .custom-input {
          background: #f5f5f5;
          border: 1px solid #a8dadc !important;
          border-radius: 10px !important;
          font-size: 14px !important;
          color: #445455 !important;
          height:40px;
          padding-left:17px;
        }
        .custom-input:focus {
          background: #f5f5f5;
        }
      `}</style>
    </>
  );
}
