import Head from "next/head";
import Link from "next/link";
import Router from "next/router";
import { Container, Row, Col, Form, Button, Image } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useState, useEffect } from "react";
import { axiosGeneral } from "../helpers/global";
import { setAccessToken, setUser, setLoading } from "../store/actionCreator";
import * as Yup from "yup";
import { useFormik } from "formik";
import { useToasts } from "react-toast-notifications";
import OTPInput, { ResendOTP } from "otp-input-react";

const FormSchema = Yup.object().shape({
  email: Yup.string().email().required("* Email tidak boleh kosong"),
  password: Yup.string().required("* Password tidak boleh kosong"),
});

export default function Home() {
  const dispatch = useDispatch();
  const accessToken = useSelector((state) => state.accessToken);
  const { addToast } = useToasts();
  const [OTP, setOTP] = useState();
  const [isOtp, setIsOtp] = useState(false);
  const [tokenTemp, setTokenTemp] = useState("");

  useEffect(() => {
    if (accessToken) {
      Router.replace("/dashboard");
    }
  }, []);

  const doLogin = async (values) => {
    try {
      dispatch(setLoading(true));
      const response = await axiosGeneral.post("/login", values);
      const { status, data } = response;
      // console.log(data);
      if (status === 200) {
        setTokenTemp(data.data.temp_token);
        dispatch(setLoading(false));
        setIsOtp(true);
        setOTP("");
      }
    } catch (error) {
      dispatch(setLoading(false));
      addToast(error.response.data.message, { appearance: "error" });
    }
  };

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: FormSchema,
    onSubmit: (values) => doLogin(values),
  });

  const renderButton = (buttonProps) => {
    return (
      <button
        className="px-5 py-2 bg-blue-300 rounded text-white mb-4 text-sm"
        {...buttonProps}
      >
        {buttonProps.remainingTime !== 0
          ? `Kirim Ulang (${buttonProps.remainingTime}s)`
          : "Kirim Ulang"}
      </button>
    );
  };

  const renderTime = (remainingTime) => {
    return <></>;
  };

  const doMatchOtp = async () => {
    try {
      const body = {
        otp: OTP,
        temp_token: tokenTemp,
      };
      dispatch(setLoading(true));

      const response = await axiosGeneral.post("/verify-otp", body);
      const { status, data } = response;
      //console.log(status);
      if (status === 200) {
        dispatch(setUser(data.data.user_metadata));
        dispatch(setAccessToken(data.data.token));
        Router.replace("/dashboard");

        dispatch(setLoading(false));
      }
    } catch (error) {
      dispatch(setLoading(false));
      addToast("Gagal melanjutkan", { appearance: "error" });
      setOTP("")
    }
  };

  const doResendOtp = async () => {
    try {
      const body = {
        temp_token: tokenTemp,
      };
      dispatch(setLoading(true));

      const response = await axiosGeneral.post("/resend-otp", body);

      const { status, data } = response;
      if (status === 200) {
        addToast("Silahkan cek email anda", { appearance: "success" });
        dispatch(setLoading(false));
      }
    } catch (error) {
      dispatch(setLoading(false));
      addToast("kesalahan request data", { appearance: "error" });
    }
  };

  return (
    <>
      <Head>
        <title>Login - E-Survey</title>
      </Head>
      <Container fluid>
        <Row className="overflow-hidden">
          <Col className="col-left" xl="3" lg="3">
            {isOtp == false ? (
              <>
                <div className="on-center">
                  <h1>E-Survey</h1>
                  <h3 className="font-semibold">Masuk</h3>
                  <form onSubmit={formik.handleSubmit} method="POST">
                    <Form.Group className="mt-5">
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        className="custom-input"
                        type="email"
                        autoComplete="false"
                        name="email"
                        onChange={formik.handleChange}
                        value={formik.values.email}
                      />
                      {formik.errors.email && (
                        <span className="text-xs text-red-500">
                          {formik.errors.email}
                        </span>
                      )}
                    </Form.Group>
                    <Form.Group>
                      <Form.Label htmlFor="password">Password</Form.Label>
                      <Form.Control
                        className="custom-input"
                        type="password"
                        autoComplete="true"
                        name="password"
                        onChange={formik.handleChange}
                        value={formik.values.password}
                      />
                      {formik.errors.password && (
                        <span className="text-xs text-red-500">
                          {formik.errors.password}
                        </span>
                      )}
                    </Form.Group>
                    <Link href="/forgot-password">
                      <span className="forgot-password-link">
                        Lupa Password?
                      </span>
                    </Link>
                    <Button
                      disabled={formik.isSubmitting}
                      variant="warning"
                      type="submit"
                      className="mt-4"
                      block
                    >
                      Login
                    </Button>
                  </form>
                </div>
              </>
            ) : (
              <div className="on-center">
                <ResendOTP
                  renderButton={renderButton}
                  renderTime={renderTime}
                  maxTime={90}
                  style={{ justifyContent: "center" }}
                  onResendClick={() => doResendOtp()}
                />
                <OTPInput
                  value={OTP}
                  onChange={(value) => setOTP(value)}
                  autoFocus
                  OTPLength={5}
                  otpType="number"
                  inputClassName="outline-none rounded font-bold mx-2"
                  inputStyles={{
                    color: "#000",
                    backgroundColor: "#F2F2F2",
                    width: "2.8rem",
                    height: 50,
                  }}
                  style={{
                    justifyContent: "center",
                    width: "auto",
                    marginLeft: 20,
                  }}
                />
                <div className="mt-6 w-full" onClick={() => doMatchOtp()}>
                  <Button className="w-full">Lanjutkan</Button>
                </div>
                <div className="text-xs md:text-base text-center w-full mt-4 font-semibold">
                  <span
                    className="text-blue-600"
                    type="button"
                    onClick={() => setIsOtp(false)}
                  >
                    Kembali ke halaman login
                  </span>
                </div>
              </div>
            )}
          </Col>
          <Col className="col-right" xl="9" lg="9">
            <Image
              src="https://www.imigrasi.go.id/uploads/logo/10-52-13-logo-imigrasi_-_Copy.png"
              alt="Logo Imigrasi"
              className="imigrasi-logo mx-auto"
              fluid
            />
          </Col>
        </Row>
      </Container>
      <style>{`
        .forgot-password-link {
          font-size: 13px;
          color: #4361EE;
          text-align: right;
          display:block;
          cursor: pointer;
        }
        .imigrasi-logo {
          width:90%;
          height: 50vh;
          object-fit: contain;
          margin-top:20vh;
          margin-left: auto;
          margin-right: auto;
          display:block;
        }
        .on-center {
          margin-top:60%;
        }
        .col-right {
          background: #03045E;
          height: 100vh;
        }
        .col-left {
          background: #FDFDFD;
          height: 100vh;
          max-height: 100vh;
          padding-left: 36px;
          padding-right: 36px;
        }
        h1 {
          font-family: "Poppins", sans-serif;
          font-weight: bold;
          font-size: 3.75rem;
          color: #323232;
          text-align:center;
          margin-bottom: 75px;
        }
        h3 {
          font-weight: 600;
        }
        .inline-icon {
          position: absolute;
          bottom: .45rem;
          right: .45rem;
          font-size: 24px;
          cursor:pointer;
        }
        .btn-warning {
          color: #2B2D42 !important;
          font-weight: bold !important;
          border-radius: 10px;
          text-transform: uppercase;
          height:40px;
          font-size: 14px;
        }
        .custom-input {
          background: #f5f5f5;
          border: 1px solid #a8dadc !important;
          border-radius: 10px !important;
          font-size: 14px !important;
          color: #445455 !important;
          height:40px;
          padding-left:17px;
        }
        .custom-input:focus {
          background: #f5f5f5;
        }
      `}</style>
    </>
  );
}
